mapper.insert:
    INSERT INTO CART(
    	CREATED_DATE
		,PRICE
		,USER_ID
		,STOCK
		,SHOP_ID
    ) VALUES(
		now()
		,#{PRICE}
		,#{USER_ID}
		,#{STOCK}
		,#{SHOP_ID}
    )

mapper.update:
    UPDATE CART SET
   		SHOP_ID = #{shop_id}
    	,STOCK = #{stock}
    	,PRICE = #{price}
	WHERE 1=1
    	AND CART_ID = #{cart_id}
    	AND USER_ID = #{user_id}

mapper.delete:
DELETE FROM CART 
WHERE 1=1
	AND CART_ID = #{cart_id}
	AND USER_ID = #{user_id}
