mapper.update:
    UPDATE CONTENTS SET
    	CATEGORY_CODE = #{contents_code}
		,TITLE = #{title}
		,CONTENTS = #{contents}
		,IS_ENABLE = #{is_enable}
		,MAIN_IMG = #{main_img}
    WHERE 1=1
    	AND CONTENTS_ID = #{contents_id}

mapper.delete:
DELETE FROM CONTENTS 
WHERE 1=1
	AND CONTENTS_ID = #{contents_id}
