mapper.update:
    UPDATE SHOP SET
    	TITLE             = #{title}
		,CONTENTS          = #{contents}
		,PRICE             = #{price}			
		,IS_ENABLE         = #{is_enable}
		,CATEGORY_CODE	=	 #{category_code}				
		,POINT             = #{point}
		,USER_ID           = #{user_id}
		,SIZE              = #{size}
		,STOCK             = #{stock}
		,BRAND             = #{brand}
		,MAIN_IMG		   = #{main_img}
	WHERE 1=1
    	AND SHOP_ID = #{shop_id}

mapper.delete:
DELETE FROM SHOP 
WHERE 1=1
	AND SHOP_ID = #{shop_id}
